def minReverseOperations(n: int, p: int, banned: List[int], k: int) -> List[int]:
    banned = set(banned)
    ans = [-1] * n
    for i in range(n):
        if i in banned:
            continue
        if i == p:
            ans[i] = 0
            continue
        if abs(i - p) % k == 0:
            ans[i] = abs(i - p) // k
