def minimumScore(s: str, t: str) -> int:
    n, m = len(s), len(t)
    left, right = [0]*m, [0]*m
    j = 0
    for i in range(n):
        if j < m and s[i] == t[j]:
            left[j] = i
            j += 1
    if j < m: return n
    j = m - 1
    for i in range(n-1, -1, -1):
        if j >= 0 and s[i] == t[j]:
            right[j] = i
            j -= 1
    res = right[0] + 1
    for i in range(1, m):
        res = min(res, right[i] - left[i-1] + 1)
    return res

