def minimumVisitedCells(grid: List[List[int]]) -> int:
    m, n = len(grid), len(grid[0])
    heap = [(0, 0, 0)]  # (distance, row, col)
    visited = set()

    while heap:
        dist, i, j = heapq.heappop(heap)
        if (i, j) in visited:
            continue
        visited.add((i, j))
        if (i, j) == (m - 1, n - 1):
            return dist + 1
        for k in range(j + 1, min(n, j + grid[i][j] + 1)):
            if (i, k) not in visited:
                heapq.heappush(heap, (dist + 1, i, k))
        for k in range(i + 1, min(m, i + grid[i][j] + 1)):
            if (k, j) not in visited:
                heapq.heappush(heap, (dist + 1, k, j))

    return -1

