def putMarbles(weights: List[int], k: int) -> int:
    n = len(weights)
    prefix_sum = [0] * (n + 1)
    for i in range(n):
        prefix_sum[i + 1] = prefix_sum[i] + weights[i]

    dp = [[float('inf')] * (k + 1) for _ in range(n + 1)]
    dp[0][0] = 0
    for i in range(1, n + 1):
        for j in range(1, min(i, k) + 1):
            for x in range(i):
                dp[i][j] = min(dp[i][j], dp[x][j - 1] + weights[i - 1] + weights[x] - 2 * (prefix_sum[i] - prefix_sum[x]))

    max_score = max(dp[i][k] for i in range(k, n + 1))
    min_score = min(dp[i][k] for i in range(k, n + 1))
    return max_score - min_score

