def findCrossingTime(n: int, k: int, time: List[List[int]]) -> int:
    workers = sorted([(t[0] + t[2], i, t) for i, t in enumerate(time)])
    heap = [(t[0] + t[1], i, 1) for i, t in workers[:k]]
    heapq.heapify(heap)
    for _ in range(n - k):
        t, i, _ = heapq.heappop(heap)
        heapq.heappush(heap, (t + workers[i][2][0] + workers[i][2][1] + workers[i][2][2] + workers[i][2][3], i, 0))
    return max(t + workers[i][2][2] + workers[i][2][3] if f else t for t, i, f in heap)

def isReachable(targetX: int, targetY: int) -> bool:
    while targetX >= 1 and targetY >= 1:
        if targetX > targetY:
            targetX -= targetY
        else:
            targetY -= targetX
    return targetX == targetY == 1

