def maxPower(stations: List[int], r: int, k: int) -> int:
    n = len(stations)
    prefix_sum = [0] * (n + 2 * r + 1)
    for i in range(n):
        prefix_sum[i + r + 1] = stations[i]
    for i in range(1, len(prefix_sum)):
        prefix_sum[i] += prefix_sum[i - 1]
    max_power = [0] * (n + 2 * r + 1)
    deque = []
    for i in range(r + 1, n + r + 1):
        while deque and deque[0] < i - 2 * r - 1:
            deque.pop(0)
        while deque and max_power[deque[-1]] <= max_power[i - r - 1]:
            deque.pop()
        deque.append(i - r - 1)
        max_power[i] = max(max_power[i - 1], max_power[deque[0]] + prefix_sum[i] - prefix_sum[i - r])
    for i in range(n + r + 1, n + 2 * r + 1):
        max_power[i] = max(max_power[i - 1], max_power[i - r - 1] + min(k, (i - n - r) * r))
        k -= min(k, (i - n - r) * r)
    return max_power[-1]

def countAnagrams(s: str) -> int:
    from collections import Counter
    MOD = 10**9 + 7

    def countAnagram(word):
        counter = Counter(word)
        fact = 1
        for val in counter.values():
            fact *= factorial(val)
        return factorial(len(word)) // fact

    def factorial(n):
        fact = 1
        for i in range(1, n+1):
            fact = (fact * i) % MOD
        return fact

    words = s.split(' ')
    result = 1
    for word in words:
        result = (result * countAnagram(word)) % MOD

    return result

