def maxPoints(grid: List[List[int]], queries: List[int]) -> List[int]:
    m, n = len(grid), len(grid[0])
    dp = [[0]*n for _ in range(m)]
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]

    def dfs(i, j, q):
        if dp[i][j] >= q or grid[i][j] > q:
            return dp[i][j]
        dp[i][j] = q
        for dx, dy in directions:
            x, y = i + dx, j + dy
            if 0 <= x < m and 0 <= y < n:
                dfs(x, y, q)
        return dp[i][j]

    res = []
    for q in queries:
        dp = [[0]*n for _ in range(m)]
        res.append(dfs(0, 0, q))
    return res

