def splitMessage(message: str, limit: int) -> List[str]:
    if limit <= 4:
        return []
    parts = []
    total_parts = len(message) // (limit - 4) + (len(message) % (limit - 4) != 0)
    for i in range(total_parts):
        start = i * (limit - 4)
        end = min((i + 1) * (limit - 4), len(message))
        part = message[start:end]
        part += "<{}/{}>".format(i + 1, total_parts)
        if len(part) > limit:
            return []
        parts.append(part)
    return parts

def maxPalindromes(s: str, k: int) -> int:
