        return sub == sub[::-1]

    n = len(s)
    dp = [0] * (n + 1)
    for i in range(k, n + 1):
        for j in range(i):
            if is_palindrome(s[j:i]):
                dp[i] = max(dp[i], dp[j] + 1)
    return dp[n]

