def componentValue(nums: List[int], edges: List[List[int]]) -> int:
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    total = sum(nums)
    avg = total // len(nums)
    if total % len(nums) != 0:
        return -1

    def dfs(node, parent):
        curr_sum = nums[node]
        for child in graph[node]:
            if child != parent:
                child_sum, valid = dfs(child, node)
                if not valid:
                    return 0, False
                curr_sum += child_sum
        return curr_sum, curr_sum % avg == 0

    root_sum, valid = dfs(0, -1)
    return len(nums) - 1 if valid and root_sum // avg == len(nums) else -1

