def numberOfPaths(grid: List[List[int]], k: int) -> int:
    MOD = 10**9 + 7
    m, n = len(grid), len(grid[0])
    dp = [[[0]*k for _ in range(n)] for _ in range(m)]
    dp[0][0][grid[0][0] % k] = 1

    for i in range(m):
        for j in range(n):
            if i == j == 0:
                continue
            for mod in range(k):
                if i > 0:
                    dp[i][j][(mod+grid[i][j])%k] += dp[i-1][j][mod]
                    dp[i][j][(mod+grid[i][j])%k] %= MOD
                if j > 0:
                    dp[i][j][(mod+grid[i][j])%k] += dp[i][j-1][mod]
                    dp[i][j][(mod+grid[i][j])%k] %= MOD
    return dp[-1][-1][0]
