class Solution:
    def findMaximumElegance(self, items: List[List[int]], k: int) -> int:
        profits = [item[0] for item in items] 
        categories = [item[1] for item in items]

        n = len(items)
        dp = [[0] * (n+1) for _ in range(k+1)]

        for i in range(1, k+1):
            for j in range(i, n+1):
                max_elegance = 0
                distinct_cats = set()

                for l in range(j-1, i-2, -1):
                    distinct_cats.add(categories[l])
                    elegance = dp[i-1][l] + profits[j-1] + len(distinct_cats)**2
                    max_elegance = max(max_elegance, elegance)

                dp[i][j] = max_elegance

        return dp[k][n]

