    def modifiedGraphEdges(self, n: int, edges: List[List[int]], source: int, destination: int, target: int) -> List[List[int]]:
        from collections import defaultdict
        import heapq

        graph = defaultdict(list)
        for u, v, w in edges:
            graph[u].append((v, w))
            graph[v].append((u, w))

        q = [(0, source)]
        dist = {source: 0}
        while q:
            d, u = heapq.heappop(q)
            if u == destination:
                break
            if d > dist[u]:
                continue
            for v, w in graph[u]:
                if w < 0:
                    continue
                if v not in dist or dist[v] > d + w:
                    dist[v] = d + w 
                    heapq.heappush(q, (dist[v], v))

        if dist[destination] != target:
            return []

        res = []
        for u, v, w in edges:
            if w < 0:
                res.append([u, v, target - dist[v]]) 
            else:
                res.append([u, v, w])
        return res

