class Solution:
    def minimumTotalPrice(self, n: int, edges: List[List[int]], price: List[int], trips: List[List[int]]) -> int:
        graph = defaultdict(list)
        for u, v in edges:
            graph[u].append(v)
            graph[v].append(u)

        def dfs(node, parent):
            nonlocal price
            if node != 0:
                price[node] //= 2
            for child in graph[node]:
                if child != parent:
                    dfs(child, node)

        dfs(0, -1)

        def find_path_price(start, end):
            visited = set()
            stack = [(start, 0)]
            while stack:
                node, cost = stack.pop()
                if node == end:
                    return cost
                if node in visited:
                    continue
                visited.add(node)
                for child in graph[node]:
                    stack.append((child, cost + price[child]))
            return 0

        total_price = 0
        for start, end in trips:
            total_price += find_path_price(start, end)
        return total_price

