class Solution:
    def cycleLengthQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        graph = defaultdict(set)
        for i in range(2, 2**n):
            graph[i//2].add(i)
            graph[i].add(i//2)

        def dfs(node, parent):
            visited[node] = True
            cycle.append(node)
            for child in graph[node]:
                if child == parent:
                    continue
                if visited[child]:
                    cycle_start = cycle.index(child)
                    return cycle[cycle_start:]
                path = dfs(child, node)
                if path:
                    return path
            cycle.pop()
            return []

        ans = []
        for u, v in queries:
            visited = [False] * (2**n)
            cycle = []
            path = dfs(u, -1)
            ans.append(len(path))
            graph[u].add(v)
            graph[v].add(u)
        return ans

