class Solution:
    def minExtraChar(self, s: str, dictionary: List[str]) -> int:
        n = len(s)
        dp = [n] * (n+1)
        dp[0] = 0

        for i in range(n):
            for w in dictionary:
                if s[i-len(w)+1:i+1] == w:
                    dp[i+1] = min(dp[i+1], dp[i-len(w)+1])

        return dp[n]

