class Solution:
    def getSubarrayBeauty(self, nums: List[int], k: int, x: int) -> List[int]:
        n = len(nums)
        res = []
        for i in range(n - k + 1):
            sub = nums[i:i+k]
            negs = [num for num in sub if num < 0]
            beauty = -sorted(negs)[x-1] if len(negs) >= x else 0
            res.append(beauty)
        return res

