class Solution:
    def isPossibleToCutPath(self, grid: List[List[int]]) -> bool:
        m, n = len(grid), len(grid[0])

        def dfs(i, j):
            if i < 0 or j < 0 or i >= m or j >= n or grid[i][j] == 0:
                return False
            if i == m-1 and j == n-1:
                return True
            grid[i][j] = 0
            res = dfs(i+1, j) or dfs(i, j+1) 
            grid[i][j] = 1
            return res

        return not dfs(0, 0)

