class Solution:
    def makeStringsEqual(self, s: str, target: str) -> bool:
        if s == target:
            return True

        n = len(s)
        for i in range(n):
            for j in range(i+1, n):
                s_new = list(s)
                s_new[i] = str(int(s[i]) | int(s[j])) 
                s_new[j] = str(int(s[i]) ^ int(s[j]))
                s = "".join(s_new)

                if s == target:
                    return True

        return False

