class Solution:
    def takeCharacters(self, s: str, k: int) -> int:
        counts = [0] * 26
        for c in s:
            counts[ord(c) - ord('a')] += 1

        left = 0
        right = len(s) - 1
        res = 0
        while True:
            can_take_all = True
            for i in range(26):
                if counts[i] < k:
                    can_take_all = False
                    break
            if can_take_all:
                return res

            if s[left] != s[right]:
                if counts[ord(s[left]) - ord('a')] < counts[ord(s[right]) - ord('a')]:
                    counts[ord(s[left]) - ord('a')] += 1
                    left += 1
                else:
                    counts[ord(s[right]) - ord('a')] += 1
                    right -= 1
            else:
                counts[ord(s[left]) - ord('a')] += 1
                left += 1
                right -= 1
            res += 1

        return -1

