                currSum += nums[i]
                seen.add(nums[i])
        maxSum = max(maxSum, currSum)

        for i in range(k, n):
            if nums[i-k] in seen:
                currSum -= nums[i-k]
                seen.remove(nums[i-k])
            if nums[i] not in seen:
                currSum += nums[i]
                seen.add(nums[i])
            maxSum = max(maxSum, currSum)

