class Solution:
    def productQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        powers = []
        i = 0
        while n > 0:
            if n & 1:
                powers.append(2**i)
            i += 1
            n >>= 1

        prefix = [1]
        for p in powers:
            prefix.append(prefix[-1] * p)

        mod = 10**9 + 7
        ans = []
        for left, right in queries:
            ans.append(prefix[right+1] // prefix[left] % mod)
        return ans

