class Solution:
    def makeSmallestPalindrome(self, s: str) -> str:
        n = len(s)
        left, right = 0, n-1

        while left < right:
            if s[left] == s[right]:
                left += 1
                right -= 1
            else:
                break

        if left >= right:
            return s

        suffix = s[left:right+1]
        reverse = suffix[::-1]

        for i in range(len(suffix)):
            if suffix[i] != reverse[i]:
                break

        new_mid = min(suffix[i], reverse[i]) 
        smallest = s[:left] + new_mid + reverse[:i] + new_mid + s[right+1:]

        return smallest

