class Solution:
    def splitMessage(self, message: str, limit: int) -> List[str]:
        parts = []
        n = len(message)

        num_parts = (n-1) // limit + 1
        for i in range(num_parts):
            start = i * limit
            end = min(start + limit, n)
            part = message[start:end]
            suffix = f"<{i+1}/{num_parts}>"
            part += suffix
            parts.append(part)

        if len(parts[-1]) > limit:
            return []

        return parts

