                dp[i][i+1] = 1

        for length in range(3, n+1):
            for i in range(n - length + 1):
                j = i + length - 1
                if s[i] == s[j] and dp[i+1][j-1]:
                    dp[i][j] = 1

        curr_len, ans = 0, 0
        for length in range(k, n+1):
            for i in range(n - length + 1):
                j = i + length - 1
                if dp[i][j]:
                    if curr_len + length - 1 > j:
                        curr_len = j - i + 1
                    else:
                        curr_len += length - 1
                    ans = max(ans, curr_len)

