class Solution:
    def componentValue(self, nums: List[int], edges: List[List[int]]) -> int:
        n = len(nums)
        graph = defaultdict(list)
        for u, v in edges:
            graph[u].append(v)
            graph[v].append(u)

        def dfs(node, parent):
            subtree_sum = nums[node]
            for child in graph[node]:
                if child != parent:
                    subtree_sum += dfs(child, node)
            return subtree_sum

        total_sum = dfs(0, -1)

        def dfs2(node, parent):
            best = 0
            subtree_sum = nums[node]
            for child in graph[node]:
                if child != parent:
                    s = dfs2(child, node)
                    subtree_sum += s
                    best = max(best, s)

            rest = total_sum - subtree_sum
            best = max(best, rest)
            return best

        return dfs2(0, -1)

