class Solution:
    def maximumRobots(self, chargeTimes: List[int], runningCosts: List[int], budget: int) -> int:
        n = len(chargeTimes)
        chargeTimes.sort()
        runningCosts.sort()

        left = 0
        right = n

        while left < right:
            mid = (left + right + 1) // 2
            if chargeTimes[mid-1] + mid * runningCosts[mid-1] <= budget:
                left = mid 
            else:
                right = mid - 1

        return left

