class Solution:
    def longestCycle(self, edges: List[int]) -> int:
        n = len(edges)
        visited = set()
        longest = 0

        def dfs(node, length):
            nonlocal longest
            visited.add(node)
            next_node = edges[node]
            if next_node not in visited:
                dfs(next_node, length + 1)
            else:
                if next_node == node:
                    longest = max(longest, length+1)
                else:
                    longest = max(longest, length)

        for i in range(n):
            if i not in visited:
                dfs(i, 0)

        return longest if longest > 2 else -1

