class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        prefix = [0]
        for num in nums:
            prefix.append(prefix[-1] + num)

        ans = 0
        for i in range(n):
            j = bisect.bisect_left(prefix, k / (i+1) + prefix[i])
            ans += j - i - 1
        return ans

