class Solution:
    def minimumObstacles(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])

        q = [(0, 0, 0)]
        visited = set()

        while q:
            i, j, obs = q.pop(0)
            if (i, j) == (m-1, n-1):
                return obs
            if (i, j) in visited:
                continue

            visited.add((i, j))

            for x, y in [(i+1, j), (i-1, j), (i, j+1), (i, j-1)]:
                if 0 <= x < m and 0 <= y < n:
                    if grid[x][y] == 1:
                        q.append((x, y, obs+1))
                    else:
                        q.append((x, y, obs))

        return -1

