class Solution:
    def totalStrength(self, strength: List[int]) -> int:
        mod = 10**9 + 7
        n = len(strength)
        prefix = [0] * (n+1)
        for i in range(n):
            prefix[i+1] = prefix[i] + strength[i]

        ans = 0
        for i in range(n):
            min_val = strength[i]
            total = prefix[i+1]
            ans += min_val * total
            ans %= mod

        for i in range(n):
            for j in range(i+1,n):
                min_val = min(strength[i:j+1])
                total = prefix[j+1] - prefix[i]
                ans += min_val * total
                ans %= mod

        return ans

