class Solution:
    def longestPath(self, parent: List[int], s: str) -> int:
        graph = defaultdict(list)
        for i, p in enumerate(parent):
            if p != -1:
                graph[p].append(i)

        def dfs(node, parent):
            max_len = 0
            for child in graph[node]:
                if child != parent:
                    length = 1 + dfs(child, node)
                    if s[child] != s[node]:
                        max_len = max(max_len, length)
            return max_len

        return dfs(0, -1)

