        self.intervals.append((left, right))

    def count(self) -> int:
        result = 0
        intervals = sorted(self.intervals)
        curr_right = -1

        for left, right in intervals:
            if left <= curr_right:
                curr_right = max(curr_right, right)
            else:
                result += curr_right - left + 1
                curr_right = right

        return result

