class Solution:
    def maximumMinutes(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])

        def in_bounds(r, c):
            return 0 <= r < m and 0 <= c < n

        def adjacent(r, c):
            for nr, nc in [(r-1, c), (r, c-1), (r+1, c), (r, c+1)]:
                if in_bounds(nr, nc):
                    yield nr, nc

        def fire_spreads():
            fire = set()
            for r, row in enumerate(grid):
                for c, val in enumerate(row):
                    if val == 1:
                        fire.add((r, c))

            while fire:
                r, c = fire.pop()
                for nr, nc in adjacent(r, c):
                    if grid[nr][nc] == 0:
                        grid[nr][nc] = 1
                        fire.add((nr, nc))

        def is_safe():
            visited = set()
            q = [(0, 0)]
            while q:
                r, c = q.pop(0)
                if (r, c) in visited:
                    continue
                if r == m-1 and c == n-1:
                    return True
                visited.add((r, c))
                for nr, nc in adjacent(r, c):
                    if grid[nr][nc] == 0 and (nr, nc) not in visited:
                        q.append((nr, nc))
            return False

        ans = 10**9
        l, r = 0, 10**9

        while l < r:
            mid = (l + r) // 2
            fire_spreads()
            if is_safe():
                ans = mid 
                r = mid
            else:
                l = mid + 1

        return ans if ans < 10**9 else -1

