# Definition for a binary tree node.
# class TreeNode:
#     def __init__(self, val=0, left=None, right=None): 
#         self.val = val
#         self.left = left
#         self.right = right

class Solution:
    def reverseOddLevels(self, root: Optional[TreeNode]) -> Optional[TreeNode]:
        if not root:
            return None

        queue = [root]
        reverse = False

        while queue:
            n = len(queue)

            for i in range(n):
                node = queue.pop(0)

                if reverse:
                    node.left, node.right = node.right, node.left

                if node.left:
                    queue.append(node.left)
                if node.right:
                    queue.append(node.right)

            reverse = not reverse

        return root

