class Solution:
    def reachableNodes(self, n: int, edges: List[List[int]], restricted: List[int]) -> int:
        graph = [[] for _ in range(n)]
        for u, v in edges:
            graph[u].append(v)
            graph[v].append(u)

        visited = [False] * n
        stack = [0]
        visited[0] = True
        count = 1

        while stack:
            node = stack.pop()
            for nei in graph[node]:
                if nei not in restricted and not visited[nei]:
                    visited[nei] = True
                    stack.append(nei)
                    count += 1

        return count

