            d += 1

        curr = node2
        d = 0
        while curr != -1:
            dist2[curr] = d
            curr = edges[curr]
            d += 1

        min_node = -1
        min_dist = float('inf')
        for node in dist1:
            if node in dist2:
                max_dist = max(dist1[node], dist2[node])
                if max_dist < min_dist:
                    min_dist = max_dist
                    min_node = node

        return min_node

