class Solution:
    def largestWordCount(self, messages: List[str], senders: List[str]) -> str:
        sender_counts = {}
        for i, message in enumerate(messages):
            sender = senders[i]
            count = len(message.split())
            if sender not in sender_counts:
                sender_counts[sender] = count 
            else:
                sender_counts[sender] += count

        max_count = max(sender_counts.values()) 
        max_senders = [s for s in sender_counts if sender_counts[s] == max_count]
        return sorted(max_senders, reverse=True)[0]

