class Solution:
    def maximumWhiteTiles(self, tiles: List[List[int]], carpetLen: int) -> int:
        tiles.sort()
        n = len(tiles)
        presum = [0] * (n + 1)
        for i in range(n):
            presum[i+1] = presum[i] + tiles[i][1] - tiles[i][0] + 1

        ans = 0
        for i in range(n):
            length = min(tiles[i][1] - tiles[i][0] + 1, carpetLen)
            ans = max(ans, presum[i+1] - presum[max(0, i - carpetLen + length)] + length)
        return ans

